    //         //const res = await auth.createUserWithEmailAndPassword(email, pass)
    //         const res = await auth.signInWithPopup(provider)
    //         console.log(res.user)
    //         await db.collection('EAfitBikeUsuarios').doc(res.user.uid).set({
