                        </button>
                        {/* <button 
                            className="btn btn-sm btn-info btn-block"
                            type="button"
                            onClick={()=> setEsRegistro(!esRegistro)}
                        >
                            {
                                esRegistro ? "¿Ya estas registrado?" : "¿No tienes cuenta?"
                            }
                        </button> */}
                    </form>
                </div>
            </div>
        </div>
    )
}

export default withRouter(Login)
