import React from 'react'
import { auth } from '../firebase'
import { withRouter } from 'react-router-dom'
import axios from 'axios'
import { BicicletaContext } from '../context/BicicletaProvider'
const Alquiler = (props) => {
    const [user, setUser] = React.useState(null)
    const [foto, setFoto] = React.useState(null)
    const [email, setEmail] = React.useState(null)
    const { bici, setBici } = React.useContext(BicicletaContext)
    const [ListaBicicletas, setData] = React.useState([{}])
    const [idElim, setIdElim] = React.useState("");
    const [usuario, setUsuario] = React.useState([{}]);



    React.useEffect(async () => {
        if (auth.currentUser) {
            console.log("Existe")
            console.log(auth.currentUser.providerData)
            setUser(auth.currentUser.displayName)
            setFoto(auth.currentUser.photoURL)
            setEmail(auth.currentUser.email)
        } else {
            props.history.push('/')
            setUser(null)
        }

        //const urlApi = process.env.REACT_APP_BICI_URL + "/bicicleta/";
        // const result = await axios(
        //     process.env.REACT_APP_BICI_URL + "/bicicletas",
        // );
        // setData(result.data);
        console.log(process.env.REACT_APP_BICI_URL + "/bicicletas/?estado=disponible")
        const result2 = await axios(
            process.env.REACT_APP_BICI_URL + "/bicicletas/?estado=disponible",
        );
        setUsuario(result2.data);
        
        console.log(result2.data);
    }, [setUsuario])


    // const reservar = React.useCallback((id) => {
    //     setBici({ ...bici, bici: id });
    //     props.history.push('/reservarbici/')
    //     console.log(id)
    //     setIdElim(id);
    //   }, [setIdElim]);

      function reservar(idReserva){
        
    //     console.log("info reserva")
    //     console.log(urlApi + "/reserva/" + this.state._id[0]+"/"+auth.currentUser.uid)
    //     console.log(datos)
    //     axios.post(urlApi + "/reserva/"+this.state._id[0]+"/"+auth.currentUser.uid)
    //   .then(response => {
    //     console.log(response)
    //   }).catch(error => {
    //     console.log(error)
    //   })

        console.log("hey")
        fetch(process.env.REACT_APP_RESERVAS_URL + "/reserva/"+idReserva+"/"+auth.currentUser.uid,{method:'POST'})
        .then((response) => response.json())
        .then((response) => {
            window.location.reload();
        });
    }

    return (
        <div>
            <div class="row">
                <div className="mt-1 text-center col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <img src={foto} alt="" width="100px" className="img-fluid" />
                            <h5 className="card-title">{user}</h5>
                            <p className="card-text">Email:{email}</p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="row"
            >
                <div
                    className="col-9"
                    style={{
                        marginTop: "150px",
                        marginRight: "0px",
                        marginLeft: "10px"
                    }}
                >
                    <a className="btn btn-dark mb-5" href="/reservas">
                        Lista de reservados
                    </a>
                    {/* <button
                      className="btn btn-primary mb-5 ml-5"
                      onClick={() => reservar(bici._id)}
                     >
                      Reservar
                    </button> */}
                    <table className="table">
                        <thead>
                            <th>
                                ID_bicicleta
                            </th>
                            <th>
                                Color
                            </th>
                            {/* <th>
                                Reservar
                            </th> */}
                            <th>
                                Modelo
                            </th>
                            <th>
                                Marca
                            </th>
                            <th>
                                Reservar
                            </th>
                            
                        </thead>
                        <tbody>
                        {usuario.map((bici, index) => {
              return (
                <tr key={bici._id}>
                  <td>{bici._id}</td>
                  <td>{bici.color}</td>
                  <td>{bici.tipoBicicleta}</td>
                  <td>{bici.marca}</td>
                  <td>
                  <button
                      className="btn btn-primary mb-5 ml-5"
                      onClick={() => reservar(bici._id)}
                     >
                      Reservar
                    </button>
                  </td>
                  
                   

                </tr>
              );
            })}
                        </tbody>
                    </table>
                </div>
                <div
                    className="col-3"
                    style={{
                        marginTop: "150px",
                        marginLeft: "0px",
                    }}
                >

                </div>
            </div>
        </div>



    )
}


export default withRouter(Alquiler)
