import React, { useEffect } from 'react';
import axios from 'axios';
import { withRouter } from 'react-router-dom'
import { BicicletaContext } from '../context/BicicletaProvider';


  const EditarV3 = (props) => {
  const { bici, setBici } = React.useContext(BicicletaContext)
  const [ListaBicicletas, setData] = React.useState([{}])
  const [idElim, setIdElim] = React.useState("");
  const [Bicicleta, setBicicleta] = React.useState({})
  const urlApi = process.env.REACT_APP_BICI_URL+"/bicicleta"+props.location.pathname;

  console.log(process.env.REACT_APP_BICI_URL + "/bicicletas")

  useEffect(async () => {
    
    // const result = await axios(
    //   process.env.REACT_APP_BICI_URL + "/bicicletas",
    // );
    // setData(result.data);

    // await axios.delete( urlApi+idElim);
    // console.log("eliminado")
    // console.log(urlApi+idElim)
  }, []);



//   const mostrar = React.useCallback((id) => {
//     setBici({ ...bici, bici: id });
//     props.history.push('/mostrar/')
//     console.log(id)
//   }, [props.history]);

//   const editar = React.useCallback((id, color, tipoBicicleta, lat, long, estado, usuario) => {
//     setBici({ ...bici, bici: id });

//     props.history.push('/'+id)
//   }, [props.history]);

  const editar = React.useCallback((id) => {
        console.log("hey")
        fetch(urlApi,{method:'DELETE'})
        .then((response) => response.json())
        .then((response) => {
            window.location.reload();
        });
  }, [setIdElim]);


  return (
    <form id="formulario">
      <div className="col-sm-12">
        <div class="card  mt-3 " style={{ width: 400 }}>
          {/* {
            r == 1 ? (
              <img src={montaña} class="card-img-top" width="304" height="236" />
            ) : (
              r == 2 ? (
                <img src={ruta} class="card-img-top" width="304" height="236" />
              ) : (
                <img src={ruta2} class="card-img-top" width="304" height="236" />
              )
            )
          } */}
          <div class="card-body">
            {/* <h5 class="card-title">ID:
              <input
                // name="_id"
                // value={_id}
                className="ml-2"
                type="text"
                placeholder="ID bicicleta"
                onChange={()=>changeHandler()}
              />
            </h5> */}
            <h5 class="card-title">MODELO:
              <input
                // name="tipoBicicleta"
                // value={tipoBicicleta}
                className="ml-2"
                type="text"
                placeholder="Inserte modelo"
                onChange={(e)=>setBicicleta({ ...Bicicleta,tipoBicicleta: [e.target.value] })}
              />
            </h5>
            <h5 >MARCA:
              <input
                // name="marca"
                // value={marca}
                className="ml-4"
                type="text"
                placeholder="Inserte marca"
                onChange={(e)=>setBicicleta({...Bicicleta,marca: [e.target.value] })}
              />
            </h5>
          </div>
          <ul class="list-group list-group-flush">
            <li class="list-group-item">
              <h5 >Color:
                <input
                //   name="color"
                //   value={color}
                  className="ml-5"
                  type="text"
                  placeholder="Inserte color"
                  onChange={(e)=>setBicicleta({...Bicicleta,color: [e.target.value] })}
                />
              </h5>
            </li>
            <h3 className="text-center">ubicación</h3>
            <li class="list-group-item">
              <div className="ml-3">
                Latitud
                <input
                //   name="lat"
                //   value={lat}
                  className="ml-5"
                  type="text"
                  placeholder="Inserte color"
                  onChange={(e)=>setBicicleta({...Bicicleta,lat: [e.target.value] })}
                />
              </div>
              <div >
                Longitud:
                <input
                //   name="long"
                //   value={long}
                  className="ml-5 mt-2"
                  type="text"
                  placeholder="Inserte longitud"
                  onChange={(e)=>setBicicleta({...Bicicleta,long:[e.target.value] })}
                />
              </div>
            </li>
            <li class="list-group-item ml-3">
              Estado:
              <input
                // name="estado"
                // value={estado}
                className="ml-5"
                type="text"
                placeholder="Inserte estado"
                onChange={(e)=>setBicicleta({ ...Bicicleta,estado: [e.target.value] })}
              />
            </li>
            <button className="btn btn-warning btn-block"
              //href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
              //type="submit"
              onClick={()=>editar(bici.bici)}
            >
              Editar
            </button>
            <a className="btn btn-dark btn-block"
              //href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
              href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
            >
              Volver
            </a>
          </ul>
        </div>
      </div>
    </form>
  )
}
export default withRouter(EditarV3)
