import React from 'react'
import { BicicletaContext } from '../context/BicicletaProvider';

const EditarV2 = () => {

    const { bici } = React.useContext(BicicletaContext)
    const urlApiGet = process.env.REACT_APP_BICI_URL + "/bicicleta/" + bici.bici;
    const [Bicicleta, setBicicleta] = React.useState({});
    React.useEffect(async () => {
        const result = await axios(
          urlApiGet,
        );
        setBicicleta(result.data);
        console.log(result.data);
      }, [setBicicleta]);

      const changeHandler = (e) => {
        setBicicleta({ [e.target.name]: [e.target.value] })
      }

      const submitHandler=() =>{
        axios.patch(urlApi + "/bicicleta/" + bici.bici, Bicicleta)
      .then(response => {
        console.log(response)
      }).catch(error => {
        console.log(error)
      })
      }

    return (
        <form id="formulario" onSubmit={submitHandler()}>
          <div className="col-sm-12">
            <div class="card  mt-3 " style={{ width: 400 }}>
              {
                r == 1 ? (
                  <img src={monta침a} class="card-img-top" width="304" height="236" />
                ) : (
                  r == 2 ? (
                    <img src={ruta} class="card-img-top" width="304" height="236" />
                  ) : (
                    <img src={ruta2} class="card-img-top" width="304" height="236" />
                  )
                )
              }
              <div class="card-body">
                <h5 class="card-title">ID:
                  <input
                    name="_id"
                    value={_id}
                    className="ml-2"
                    type="text"
                    placeholder="ID bicicleta"
                    onChange={this.changeHandler}
                  />
                </h5>
                <h5 class="card-title">MODELO:
                  <input
                    name="tipoBicicleta"
                    value={tipoBicicleta}
                    className="ml-2"
                    type="text"
                    placeholder="Inserte modelo"
                    onChange={this.changeHandler}
                  />
                </h5>
                <h5 >MARCA:
                  <input
                    name="marca"
                    value={marca}
                    className="ml-4"
                    type="text"
                    placeholder="Inserte marca"
                    onChange={this.changeHandler}
                  />
                </h5>
              </div>
              <ul class="list-group list-group-flush">
                <li class="list-group-item">
                  <h5 >Color:
                    <input
                      name="color"
                      value={color}
                      className="ml-5"
                      type="text"
                      placeholder="Inserte color"
                      onChange={this.changeHandler}
                    />
                  </h5>
                </li>
                <h3 className="text-center">ubicaci칩n</h3>
                <li class="list-group-item">
                  <div className="ml-3">
                    Latitud
                    <input
                      name="lat"
                      value={lat}
                      className="ml-5"
                      type="text"
                      placeholder="Inserte color"
                      onChange={()=>changeHandler()}
                    />
                  </div>
                  <div >
                    Longitud:
                    <input
                      name="long"
                      value={long}
                      className="ml-5 mt-2"
                      type="text"
                      placeholder="Inserte longitud"
                      onChange={this.changeHandler}
                    />
                  </div>
                </li>
                <li class="list-group-item ml-3">
                  Estado:
                  <input
                    name="estado"
                    value={estado}
                    className="ml-5"
                    type="text"
                    placeholder="Inserte estado"
                    onChange={this.changeHandler}
                  />
                </li>
                <button className="btn btn-warning btn-block"
                  //href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
                  type="submit"
                >
                  Editar
                </button>
                <a className="btn btn-dark btn-block"
                  //href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
                  href={process.env.REACT_APP_ADMIN_URL+"/bicicletas"}
                >
                  Volver
                </a>
              </ul>
            </div>
          </div>
        </form>
      )
    }
  

export default EditarV2
