import React, { useEffect } from 'react';
import axios from 'axios';
import { withRouter } from 'react-router-dom'
import { BicicletaContext } from '../context/BicicletaProvider';
import { auth } from '../firebase'

const Bicicletas = (props) => {
  const { bici, setBici } = React.useContext(BicicletaContext)
  const [ListaBicicletas, setData] = React.useState([{}])
  const [idElim, setIdElim] = React.useState("");
  const [user, setUser] = React.useState(null)
  const [foto, setFoto] = React.useState(null)
  const [email, setEmail] = React.useState(null)


  console.log(process.env.REACT_APP_BICI_URL + "/bicicletas")

  useEffect(async () => {
    if (auth.currentUser) {
      console.log("Existe")
      console.log(auth.currentUser.providerData)
      setUser(auth.currentUser.displayName)
      setFoto(auth.currentUser.photoURL)
      setEmail(auth.currentUser.email)
    } else {
      setUser("Invitado")
      setFoto("data:image/png;base64,")
      setEmail("Invidado")
    }
    const urlApi = process.env.REACT_APP_BICI_URL + "/bicicleta/";
    const result = await axios(
      process.env.REACT_APP_BICI_URL + "/bicicletas",
    );
    setData(result.data);

    // await axios.delete( urlApi+idElim);
    // console.log("eliminado")
    // console.log(urlApi+idElim)
  }, []);



  const mostrar = React.useCallback((id) => {
    setBici({ ...bici, bici: id });
    props.history.push('/mostrar/')
    console.log(id)
  }, [props.history]);

  const editar = React.useCallback((id, color, tipoBicicleta, lat, long, estado, usuario) => {
    setBici({ ...bici, bici: id });

    props.history.push('/' + id)
  }, [props.history]);

  const eliminar = React.useCallback((id) => {
    console.log("hey")
    fetch(process.env.REACT_APP_BICI_URL + "/bicicleta/" + id, { method: 'DELETE' })
      .then((response) => response.json())
      .then((response) => {
        window.location.reload();
      });
    // setBici({ ...bici, bici: id });
    // props.history.push('/eliminar/')
    // console.log(id)
    // setIdElim(id);
  }, [setIdElim]);

  const agregar = (id) => {
    props.history.push('/crear/')
  }
  return (
    <div className="mt-2" id="fondo2">
      <div className="row"
      >
        <div
          className="col-9"
          style={{
            marginTop: "150px",
            marginRight: "0px",
            marginLeft: "10px"
          }}
        >
          <div className="card">
            <div className="card-body">
              <img src={foto} alt="" width="100px" className="img-fluid" />
              <h5 className="card-title">{user}</h5>
              <p className="card-text">Email:{email}</p>
            </div>
          </div>
          <button
            onClick={() => agregar(bici.bici)}
            className="btn btn-dark mb-5 ml-5">

            Agregar bicicleta
          </button>
          <table className="table ml-5  table-striped table-secondary">
            <thead>
              <tr>
                <th>ID</th>
                <th>Color</th>
                <th>Modelo</th>
                <th>Ubicaci√≥n</th>
                <th>Estado</th>
                <th>Mostrar</th>
                <th>Editar</th>
                <th>Eliminar</th>
              </tr>
            </thead>
            <tbody>
              {ListaBicicletas.map((bici, index) => {
                return (
                  <tr key={bici._id}>
                    <td>{bici._id}</td>
                    <td>{bici.color}</td>
                    <td>{bici.tipoBicicleta}</td>
                    <td>{bici.lat + "-" + bici.long}</td>
                    <td>{bici.estado}</td>

                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => mostrar(bici._id,
                          bici.color,
                          bici.tipoBicicleta,
                          bici.lat,
                          bici.long,
                          bici.estado,

                        )}
                      >
                        Mostrar
                      </button>
                    </td>

                    <td>
                      <button
                        className="btn btn-warning "
                        onClick={() => editar(bici._id)}
                      >
                        Editar
                      </button>
                    </td>
                    <td>
                      <button
                        className="btn btn-danger "
                        onClick={() => eliminar(bici._id)}
                      >
                        Eliminar
                      </button>
                    </td>


                  </tr>
                );
              })}

            </tbody>
          </table>




        </div>
        <div
          className="col-3"
          style={{
            marginTop: "150px",
            marginLeft: "0px",
          }}
        >

        </div>
      </div>
    </div>
  )
}

export default withRouter(Bicicletas)
